import React, { useState } from 'react';
import ReactDOM from 'react-dom';
import reportWebVitals from './reportWebVitals';

function EtudiantForm() {
  const [nom, setNom] = useState("");
  const [prenom, setPrenom] = useState("");
  const [age, setAge] = useState(0);
  const [etudiants, setEtudiants] = useState([]);

  function handleForm(event) {
    event.preventDefault();
    const etudiant = { "nom": nom, "prenom": prenom, "age": age };
    const etudiantsCopy = etudiants.concat(etudiant);
    setEtudiants(etudiantsCopy);
    setNom("");
    setPrenom("");
    setAge(0);
  }

  return (
    <>
      <table>
        <thead>
          <tr>
            <th>Nom</th>
            <th>Prenom</th>
            <th>Age</th>
          </tr>
        </thead>

        <tbody>
          {etudiants.map((elem, index) => (
            <tr key={index}>
              <td>{elem.nom}</td>
              <td>{elem.prenom}</td>
              <td>{elem.age}</td>
            </tr>
          ))}
        </tbody>
      </table>

      <form onSubmit={(e) => handleForm(e)}>
        <label htmlFor="nom"> Nom :{nom}</label>
        <input type="text" id="nom" value={nom} onChange={(e) => setNom(e.target.value)} />
        <br />

        <label htmlFor="prenom"> Prenom :{prenom}</label>
        <input type="text" id="Prenom" value={prenom} onChange={(e) => setPrenom(e.target.value)} />
        <br />

        <label htmlFor="age"> Age :{age}</label>
        <input type="number" id="age" value={age} onChange={(e) => setAge(e.target.value)} />
        <br />

        <input type='submit' value={"Enregistrer"} />
        <input type='reset' value={"Annuler"} />
      </form>
    </>
  );
}

const root = document.getElementById('root');
ReactDOM.render(<EtudiantForm />, root);

reportWebVitals();
