import React, { useState } from "react";
import { login } from "../services/auth.services";
import { useNavigate } from "react-router-dom";
import { addProduct } from "../services/product.services";

export function LoginForm() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();

  function handleSubmit(event) {
    event.preventDefault();
    
    // Vérification des champs vides avant de soumettre le formulaire
    if (!username || !password) {
      alert("Veuillez remplir tous les champs.");
      return;
    }

    // Appel de la fonction d'authentification
    login({ username, password })
      .then((response) => {
        // Si l'authentification réussit, rediriger vers la page des produits
        navigate("/products");
      })
      .catch((error) => {
        // En cas d'échec de l'authentification, afficher un message d'erreur
        console.error("Erreur d'authentification:", error);
        alert("Erreur d'authentification. Veuillez vérifier vos informations.");
      });
  }

  return (
    <div className="form-wrapper">
      <form onSubmit={handleSubmit}>
        <div className="container">
          <label className="form-label" htmlFor="username">
            Nom d'utilisateur:
          </label>
          <input
            className="form-control"
            type="text"
            id="username"
            onChange={(e) => setUsername(e.target.value)}
          />

          <label className="form-label" htmlFor="password">
            Mot de passe:
          </label>
          <input
            className="form-control"
            type="password"
            id="password"
            onChange={(e) => setPassword(e.target.value)}
          />

          <input className="btn btn-primary m-2" type="submit" value={"Se connecter"} />
          <input className="btn btn-danger m-2" type="reset" value={"Annuler"} />
        </div>
      </form>
    </div>
  );
}
