import { useEffect, useState } from "react";
import { deleteProductByID, getAllProducts } from "../services/product.services";
import { Link } from "react-router-dom";
import "../../src/product.css";

export function ProductList() {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    fetchProducts();
  }, []);

  async function fetchProducts() {
    try {
      const res = await getAllProducts();
      setProducts(res.data);
    } catch (error) {
      console.error("Error fetching products:", error);
    }
  }

  async function handleDeleteProduct(id) {
    try {
      await deleteProductByID(id);
      fetchProducts();
    } catch (error) {
      console.error("Error deleting product:", error);
    }
  }

  return (
    <>
      <div className="table-wrapper">
        <table className="fl-table">
          <thead>
            <tr>
              <th className="text-center">Name</th>
              <th className="text-center">Price</th>
              <th className="text-center">Action</th>
            </tr>
          </thead>
          <tbody>
            {products.map((elem, index) => (
              <tr key={index}>
                <td className="text-center">{elem.name}</td>
                <td className="text-center">{elem.price}</td>
                <td className="text-center">
                  {/* FontAwesome delete and edit icons */}
                  <i
                    className="fas fa-trash icon-rouge"
                    onClick={() => handleDeleteProduct(elem._id)}
                  ></i>
                  <i
                    className="fas fa-pencil-alt icon-bleue"
                    onClick={() => handleDeleteProduct(elem._id)}
                  ></i>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
        <Link to="/products/new" className="add-product-button">
          Nouveau Produit
        </Link>
      </div>
    </>
  );
}
